        $(window).scroll(function () {

            if ($(this).scrollTop() > offset) {
                $('.back-to-top').fadeIn(duration);
            } else {
                $('.back-to-top').fadeOut(duration);
            }

            if ($(window).scrollTop() > 100) {
                $('.bg').addClass('show');
            } else {
                $('.bg').removeClass('show');
            }
            $('.flexslider').css('max-height', $(window).height() - $('.caroufredsel_wrapper').height() + 40);
        });
      
        $(window).scroll(function () {			//checks if in homepage			if($('.mapbg').length){				scroll_start = $(this).scrollTop();				if (scroll_start > $('#nav_container').offset().top) {
					$('#signupbtn').css({'background-color': '#ff6600'});
				} else {
					$('#signupbtn').css({'background-color': 'transparent'});
				}				
				if (scroll_start < $('#formtexts').find('input').offset().top) {
					$('#signupbtn').css("display", "none");
				} else {
					$('#signupbtn').css("display", "block");
				}			}
        });
        /*Events on window resize*/
        $(window).resize(function () {
            $('.flexslider').css('max-height', $(window).height() - $('.caroufredsel_wrapper').height() + 40);
        });
        /*Carousel init*/
        var $c = $('#carousel'),
                $w = $(window);

        $c.carouFredSel({
            align: false,
            items: 10,
            scroll: {
                items: 1,
                duration: 4000,
                timeoutDuration: 0,
                easing: 'linear',
                pauseOnHover: "immediate-resume"
            }
        });

        $w.bind('resize.example', function () {
            var nw = $w.width();
            $c.width(nw * 3);
            $c.parent().width(nw);
        }).trigger('resize.example');
        /*Carousel init end*/
	});
	
