    var already_visible = false;

    if ( document.getElementById(full_name).classList.contains('show') )
       already_visible = true;
    ClearMenus();
    if (already_visible == false)
       document.getElementById(full_name).classList.toggle("show");
}

// Close the dropdown if the user clicks outside of it
window.onclick = function(e) {
  if (!e.target.matches('.dropbtn')) {
    ClearMenus();
  }
}

