					jQuery(document).ready( function() {
						jQuery("#searchform").on("submit", function(e){
							e.preventDefault();
							var word = jQuery('#searchtext').val();
							word = word.replace(/[<>{}&\"\/#]/g, ' ');
							word = word.replace(/\s+/g, ' ');
							word = word.trim();

							if (word.replace(/\s/g, '').length) {
								var chip = jQuery('.srchbar-chip').is(":visible");
								var searchopts = jQuery("#searchform input[name='searchopts']").val();
								var oriSearch = jQuery("#searchform input[name='oriSearch']").val();
								var bdt_puuid = jQuery("#bdt_puuid").val();

								if( (searchopts == 'search_evo' && chip ) || ( searchopts &&  !chip) || ( !searchopts &&  !chip )) {
									var filetype = jQuery('#filetypechkbox').val();

									var url = "https://www.123rf.com";

									if(searchopts == 'search_evo') {
										filetype = 1;
									} else {
										searchopts = '';
									}

									switch(filetype) {
										case "0" :
										case "1" :url += "/stock-photo/";
											break;
										case "2" : url += "/clipart-vector/";
											break;
										case "5" :
										case "6" : url += "/stock-footage/";
											break;
										case "7" : url += "/stock-audio/";
											break;
										default : url += "/stock-photo/";
											break;
									}

									word = word.replace(/ /g,"_");
									url += encodeURIComponent(word.toLowerCase()) + ".html";

									urlparam_arr = {"searchopts" : searchopts, "imgtype" : filetype, "oriSearch" : oriSearch, "sti" : bdt_puuid }

									for (var key in urlparam_arr) {
							     		if(!urlparam_arr[key]) {
							     			delete urlparam_arr[key];
							     		}
									}

									urlparam = jQuery.param(urlparam_arr);

									url += "?" + urlparam;

									window.location.href = url;

								} else {
									jQuery("#searchform")[0].submit();
								}
							} else {
								alert("Please enter a keyword.");
								jQuery('#searchtext').focus();
								return false;
							}
						});
					});
				</script>
