	var srcOff = "//static.earthcam.com/images/holiday_logos/ec_logo_15.jpg";
	
	//theLogo.hide('fast');
	
	if (dowhat == 'on')
	{
		theLogo.css('display','none');
		theLogo.attr("src", srcOn);
		theLogo.fadeIn('fast');
	}
	else
	{		
		theLogo.css('display','none');
		theLogo.attr("src", srcOff);;
		theLogo.fadeIn('fast');
	}
	//theLogo.fadeIn('fast')
}

