<script type="text/javascript">
  if ($(window).width() > 1024){
    $(window).on('scroll', function (){
      var scrollHeight = ($(window).height() / 2);
      var offsetTop = $("#flytop").offset().top - 100;
      var absHeight = $(window).height();
      
      if ($(window).scrollTop() > scrollHeight) {
          $('#flytop').stop(true,true).animate({opacity: 1}, "slow");
          $('#flytop').css('display', 'block');
      }else if (absHeight > offsetTop) {
          $('#flytop').stop(true,true).animate({opacity: 0}, "slow");
          $('#flytop').css('display', 'none');
      };            
    });
  };
  $("#flytop .flytop").click(function(event) {
    $('html,body').stop(true,true).animate({scrollTop: '0px'}, "slow",function(){
      if ($('.leftnav').length == 1 ) {
        $("#container .leftnav dd").removeClass('active');
      };
    });    
  });
</script>
