func=func+")";func=encodeURIComponent(func);var xmin=document.forms["graphForm"+gid]["xmin"+gid].value;var xmax=document.forms["graphForm"+gid]["xmax"+gid].value;var ymin=document.forms["graphForm"+gid]["ymin"+gid].value;var ymax=document.forms["graphForm"+gid]["ymax"+gid].value;setbordercolor("xmin"+gid,"#707070");setbordercolor("xmax"+gid,"#707070");setbordercolor("ymin"+gid,"#707070");setbordercolor("ymax"+gid,"#707070");if (!isNumber(xmin)){setbordercolor("xmin"+gid,"red");ok=false };if (!isNumber(xmax)){setbordercolor("xmax"+gid,"red");ok=false };if ((ymin==null || ymin=="")&& (ymax==null || ymax=="")){ymin="0"; ymax="0";} else {if (!isNumber(ymin)){setbordercolor("ymin"+gid,"red");ok=false }; if (!isNumber(ymax)){setbordercolor("ymax"+gid,"red");ok=false };}
if (!ok){document.getElementById("graphhelp"+gid).style.visibility="visible"; return false;}
cmd="fnct='"+func+"'&xmin="+xmin+"&xmax="+xmax+"&ymin="+ymin+"&ymax="+ymax;mcnet.Ajax("/inc/getgraph.php",cmd, function (text){document.getElementById("graphresult"+gid).innerHTML=text;} );return false;}
mcnet.ShowHidePassword = function()
{if (document.MyForm.passChange.checked){document.MyForm.password.type='text';} else {document.MyForm.password.type='password';}}
mcnet.AddWindowEventHandler = function (type, eventHandle)
{if (window.addEventListener){ window.addEventListener( type, eventHandle, false );} else if (window.attachEvent){window.attachEvent( "on" + type, eventHandle );}}
mcnet.AddEventHandler = function(elementid, type, eventHandle)
{var element=document.getElementById(elementid);if (element.addEventListener){ element.addEventListener( type, eventHandle, false );} else if (element.attachEvent){element.attachEvent( "on" + type, eventHandle );}}
mcnet.CleanString = function(string)
{ var s=string.replace("<","&lt;"); s=s.replace("?","&#63;"); s=s.replace("'","&#039;"); s=s.replace('"',"&quot;"); return s; }
mcnet.SetElementText = function(element,text)
{document.getElementById(element).innerHTML=text;}
mcnet.popUp = function(ww,wh,URL){window.open(URL, 'mcnetpopup', 'toolbar=0,scrollbars=1,location=0,statusbar=1,menubar=0,resizable=1,width='+ww+',height='+wh+',left = 100,top = 50');}
